#define			ZeroVariable				(long)0#define			ZeroPointer					(long)0#define			ZeroHandle					(long)0#define			ErrorMessageDialogID		1000#define			FrontCGPort					(WindowPtr)-1L//GlobalRoutinesvoid			ErrorMessageReturn			(Str255);void ErrorMessageReturn(Str255 ErrorMessage){		register	DialogPtr	DialogPort;	short					ItemList;	register	Boolean		Done;		Done = false;	if((DialogPort = GetNewDialog(ErrorMessageDialogID,ZeroPointer,FrontCGPort)) != ZeroPointer){		SetDialogDefaultItem(DialogPort,1);		ParamText(ErrorMessage,"\p","\p","\p");		ShowWindow(DialogPort);		while(Done == false){			ModalDialog(ZeroPointer,&ItemList);			switch(ItemList){				case 1:					Done = true;				default:					break;			}		}		HideWindow(DialogPort);		DisposeDialog(DialogPort);	}	return;}/*ErrorMessageReturn*/