#include <Threads.h>//GlobalRoutinesvoid		ToolBoxInit		(void);OSErr		ThreadInit		(void);short		GetApplResID	(void);Boolean		ThreadManagerOK	(void);Boolean		MIDIManagerOK	(void);Boolean		QuickTimeOK		(void);void		CopyStringByteX	(register unsigned char*,register unsigned char*);//StaticMemoriesstatic		short			ApplicationID;void ToolBoxInit(void){	ApplicationID = CurResFile();	MaxApplZone();	MoreMasters();	InitGraf(&qd.thePort);	InitFonts();	InitWindows();	InitMenus();	TEInit();	InitDialogs(0);	InitCursor();	FlushEvents(everyEvent,0);}/*ToolBoxInit*/OSErr ThreadInit(void){	return(CreateThreadPool(kCooperativeThread,2,0));}/*ThreadInit*/short GetApplResID(void){	return(ApplicationID);}/*GetApplResID*/Boolean ThreadManagerOK(void){	OSErr			Error;	long			Result;	Error = Gestalt(gestaltThreadMgrAttr,&Result);	if(Error != noErr || BitTst(&Result,31 - gestaltThreadMgrPresent) == false) return(false);	else return(true);	}/*ThreadManagerOK*/Boolean MIDIManagerOK(void){	NumVersion		MidiManagerVers;		MidiManagerVers = MIDIVersion();	if(MidiManagerVers.majorRev == 0) return(false);	else return(true);}/*MIDIManagerOK*/Boolean QuickTimeOK(void){	OSErr			Error;	long			Result;		Error = Gestalt(gestaltQuickTime,&Result);	if(Error != noErr || (Result >> 16) < 0x0250) return(false);	else return(true);}/*QuickTimeOK*/void CopyStringByteX(register unsigned char *String,register unsigned char *Written){	register	short	ID;	register	short	StringLength;		StringLength = *String;	for(ID=0;ID<=StringLength;ID++,Written++,String++) *Written = *String;	return;}/*CopyStringByteX*/