//StaticRoutinesvoid						main					(void);//ExternRoutinesextern	void				MainEventAction			(void);extern	void				SetApplicationQuit		(Boolean);extern	void				ToolBoxInit				(void);extern	OSErr				ThreadInit				(void);extern	OSErr				SetupAppleEvent			(void);extern	void				ResetAppleEvent			(void);extern	OSErr				SetupMenu				(void);extern	void				ResetMenu				(void);extern	OSErr				SetupWindow				(void);extern	void				ResetWindow				(void);extern	OSErr				SetupGWorld				(void);extern	void				ResetGWorld				(void);extern	void				VisualMainGraphic		(void);extern	OSErr				SetupMIDIPort			(void);extern	void				ResetMIDIPort			(void);extern	ComponentResult		SetupNoteAllocator		(void);extern	void				ResetNoteAllocator		(void);extern	void				ResetMidiDevice			(void);		//Midi Initextern	void				LoadPreference			(void);extern	void				SavePreference			(void);extern	Boolean				ThreadManagerOK			(void);extern	Boolean				MIDIManagerOK			(void);extern	Boolean				QuickTimeOK				(void);extern	void				ErrorMessageReturn		(Str255);	//ErrorHandlingextern	OSErr				SetupThread				(void);		//Threadextern	void				ResetThread				(void);void main(void){		ToolBoxInit();		if(ThreadManagerOK() == true){		if(ThreadInit() == noErr){			if(MIDIManagerOK() == true){				if(QuickTimeOK() == true){					if(SetupAppleEvent() == noErr){						if(SetupMenu() == noErr){							if(SetupMIDIPort() == noErr){								if(SetupNoteAllocator() == noErr){									LoadPreference();																		if(SetupWindow() == noErr){										if(SetupGWorld() == noErr){											VisualMainGraphic();																						/****** イベントルート ******/																						ResetMidiDevice();																						if(SetupThread() == noErr){												SetApplicationQuit(false);												MainEventAction();																								ResetThread();											}											else ErrorMessageReturn("\pWhile setting Thread Manager.");																						/****** ルート終わり ******/																						ResetGWorld();										}										else ErrorMessageReturn("\pWhile setting GWorld.");										ResetWindow();									}									else ErrorMessageReturn("\pWhile setting Window.");									SavePreference();																		ResetNoteAllocator();								}								else ErrorMessageReturn("\pWhile setting QuickTime.");								ResetMIDIPort();							}							else ErrorMessageReturn("\pWhile setting MIDI Port.");							ResetMenu();						}						else ErrorMessageReturn("\pWhile setting Menu.");						ResetAppleEvent();					}					else ErrorMessageReturn("\pWhile setting AppleEventProc.");				}				else ErrorMessageReturn("\pThis program needs QuickTimeｪ 2.5 or later.");			}			else ErrorMessageReturn("\pThis program needs Apple MIDI Manager.");		}		else ErrorMessageReturn("\pWhile initializing Thread Manager.");	}	else ErrorMessageReturn("\pThis program needs Thread Manager.");	ExitToShell();}/*main*/